(()=>{"use strict";const e=document.querySelector(".header"),t=e.clientHeight,o=document.querySelector(".header__subnav"),l=document.querySelector(".header__burger"),c=document.querySelectorAll(".header__lst"),s=document.querySelectorAll(".header__wrap"),r=document.querySelectorAll(".arrow");document.onscroll=function(){window.scrollY>t?(e.classList.add("header__fixed"),document.body.style.marginTop=`${t}px`):(e.classList.remove("header__fixed"),document.body.removeAttribute("style"))},l.addEventListener("click",(()=>{o.classList.toggle("active-subnav"),l.classList.toggle("active"),Boolean("active-subnav")?(document.body.style.overflow="hidden",console.log(Boolean("active-subnav"))):(document.body.style.overflow="visible",console.log("&_&"))}));for(let e=0;e<s.length;e++){let t=s[e],o=c[e],l=r[e];t.addEventListener("click",(()=>{o.classList.toggle("active-menu"),l.classList.toggle("open")}))}})();